// Gate 30 Minutes Since First (for testing)
const THRESHOLD_MS = 3 * 24 * 60 * 60 * 1000; // 3 days

function parseDateFlexible(v) {
  if (!v) return null;
  if (typeof v === 'number') return new Date(v); // epoch millis
  const s = String(v).trim();

  // epoch detection
  if (/^\d{13}$/.test(s)) return new Date(Number(s));
  if (/^\d{10}$/.test(s)) return new Date(Number(s) * 1000);

  // try ISO first (Google Sheets usually gives this format)
  const isoDate = new Date(s);
  if (!isNaN(isoDate)) return isoDate;

  // fallback: dd/mm/yyyy or dd-mm-yyyy
  const m = s.match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{4})(?:[ T](\d{1,2}):(\d{2})(?::(\d{2}))?)?$/);
  if (m) {
    const dd = +m[1], mm = +m[2], yyyy = +m[3];
    const hh = +(m[4]||0), mi = +(m[5]||0), ss = +(m[6]||0);
    return new Date(yyyy, mm-1, dd, hh, mi, ss);
  }

  return null;
}

const nowMs = Date.now();
return items.filter(item => {
  const v = item.json['First Email Sent At'] || item.json['First Email Sent at'] || item.json['First Email Sent Timestamp'];
  const d = parseDateFlexible(v);

  if (!d) {
    item.json.debug_note = `Invalid date: ${v}`;
    return false;
  }

  const diff = nowMs - d.getTime();
  item.json.debug_note = `FirstEmail=${v}, DiffMins=${(diff/60000).toFixed(2)}`;
  return diff >= THRESHOLD_MS;
});

